    private void gajiButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_gajiButtonActionPerformed
        // TODO add your handling code here:
        String nama = fieldNama.getText();
        String usia = fieldUsia.getText();
        String gaji = fieldGaji.getText();
        String lembur = fieldLembur.getText();
        
        TotalGaji a = new TotalGaji();
        a.fieldNama.setText(nama);
        a.fieldUsia.setText(usia);
        a.fieldGaji.setText(gaji);
        double gajii = Double.parseDouble(fieldGaji.getText());   
        double lemburr = Double.parseDouble(fieldLembur.getText());
        
        double TotalGaji = gajii + lemburr * 25000;
        String total = String.valueOf(TotalGaji);
        a.fieldTotalGaji.setText(total);
        
        a.setVisible(true);
        setVisible(false);
    }//GEN-LAST:event_gajiButtonActionPerformed

    private void hapusButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_hapusButtonActionPerformed
        // TODO add your handling code here:
        String nama = fieldNama.getText();
        try{
            Connection c = Koneksi.getKoneksi();
            Statement s = c.createStatement();
            try{
                String sql = "DELETE FROM karyawan WHERE Nama = '"+nama+"'";
                PreparedStatement p = c.prepareStatement(sql);
                p.executeUpdate(sql);
                p.close();
                JOptionPane.showMessageDialog(null, "Sukses menghapus data siswa!");
            }catch(SQLException e){
                System.out.println("Error");
            }
            s.close();
        }catch(Exception e){
            System.out.println("Check error");
        }
    }//GEN-LAST:event_hapusButtonActionPerformed

